import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css'

export default function RegistrationPage() {
    const navigate = useNavigate();
    const [Input, setInput] = useState({
        username: "",
        password1: ""
    });

    const verified = true;

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(Input.username)
        console.log(Input.password1)
        //to-do verifcation(2pt)


        console.log(verified)
        if (verified===true){
            localStorage.setItem("user", JSON.stringify(Input));
            navigate('/MainPage')
        }
    };


    return (
        <div className='RegFormOuter'>
            <form
                className='RegFormContainer'
                method="GET"
                id="RegistrationPage"
                // action to change
                action="formProcessor.html"
                // return validation, should I put the validation into submitHandler?
                // onsubmit="return Validation()"
                // onSubmit={submitHandler}
                onSubmit = {handleSubmit}
                target="_blank"
            >
                <div className='RegFormWrapper'>
                    <h1> Login</h1>
                    <div>
                        <p>
                            {" "}
                            username: <br />{" "}
                            <input
                                type="type"
                                value={Input.username}
                                onChange={(e) =>
                                    setInput({
                                        ...Input,
                                        [e.target.name]: e.target.value,
                                    })
                                }
                                // onChange={changeHandler}
                                name="username"
                                id="username"
                                placeholder="abc123"
                                required=""
                            />{" "}
                        </p>
                    </div>
                    <div>
                        {/*todo: password vailidation*/}
                        <p>
                            {" "}
                            password <br />{" "}
                            <input
                                type="password"
                                name="password1"
                                value={Input.password1}
                                onChange={(e) =>
                                    setInput({
                                        ...Input,
                                        [e.target.name]: e.target.value,
                                    })
                                }
                                id="password1"
                                placeholder="password"
                                defaultValue=""
                                required=""
                            />{" "}
                        </p>
                    </div>
                    <div>
                        <input className='Button' type="submit" defaultValue="submit" />
                    </div>
                </div>
            </form>
        </div>
    )
}
