import React, { useState } from 'react'
import "./MainPage.css";
import RiceOwl from './RiceOwl.png';

import Topbar from "../components/topbar";
import Post from "../components/Post"
import NewPost from "../components/NewPost"
import Status from '../components/Status';

import { Posts, PostsLeft } from "../Data"

export default function MainPage() {

    const postarray = Posts
    const postarrayleft = PostsLeft
    const [PostArray, SetPostPrray] = useState(PostsLeft)

    return (

        <>
            <header>

                <Topbar />
                <div className="container">
                    <NewPost postleft = {PostArray} setprops={SetPostPrray} />
                    <Status />
                </div>
            </header>
            <div className="pageformat">
                <div className="gridformat">
                    <div className="gridpost" tabIndex={0}>
                        {PostArray.map((p) => (
                            <Post key={p.index} post={p} />
                        ))}
                    </div>
                </div>
                <div className="gridformat">
                    <div className="gridpost" tabIndex={0}>
                        {postarray.map((p) => (
                            <Post key={p.index} post={p} />
                        ))}
                    </div>
                </div>

            </div>

        </>

    )
}
