import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Users, Posts } from '../Data';
import './LandingPage.css'

export default function RegistrationPage() {
    const navigate = useNavigate();
    const navigate2 = useNavigate();
    const [Input, setInput] = useState({
        username: "",
        password1: ""
    });



    const userarray = Users.map(({ username }) => username)
    const passwordarray = Users.map(({ password }) => password)


    const handleSubmit = (e) => {
        var verified = false;
        e.preventDefault();
        console.log(Input.username)
        console.log(Input.password1)
        //to-do verifcation(2pt)

        for (let i = 0; i < userarray.length; i++) {
            console.log(userarray[i], passwordarray[i])
            if (Input.username == userarray[i] && Input.password1 == passwordarray[i]) {
                console.log("found")
                verified = true;
            }
        }
        console.log(verified)
        if (verified === true) {
            localStorage.setItem("user", JSON.stringify(Input));
            localStorage.setItem("username", Input.username);
            localStorage.setItem("password1", Input.password1);


            localStorage.setItem("displayname", Input.username);
            localStorage.setItem("email", "abc@rice.com");
            localStorage.setItem("phone", "123-456-7891");
            localStorage.setItem("birthday", "1998-11-06");
            localStorage.setItem("zipcode", "77005");

            localStorage.setItem("password2", Input.password1);
            localStorage.setItem("newuser", 0);

            navigate('/MainPage')
        } else {
            window.alert("not registered user or wrong login information")
        }
    };


    return (
        <div className='RegFormOuter'>
            <form
                className='RegFormContainer'
                // method="GET"
                id="RegistrationPage"
                // action to change
                // action="formProcessor.html"
                // return validation, should I put the validation into submitHandler?
                // onsubmit="return Validation()"
                // onSubmit={submitHandler}
                onSubmit={handleSubmit}
                target="_blank"
            >
                <div className='RegFormWrapper'>
                    <h1> Login</h1>
                    <div>
                        <p>
                            {" "}
                            username: <br />{" "}
                            <input
                                type="type"
                                value={Input.username}
                                onChange={(e) =>
                                    setInput({
                                        ...Input,
                                        [e.target.name]: e.target.value,
                                    })
                                }
                                // onChange={changeHandler}
                                name="username"
                                id="username"
                                placeholder="abc123"
                                required=""
                            />{" "}
                        </p>
                    </div>
                    <div>
                        {/*todo: password vailidation*/}
                        <p>
                            {" "}
                            password <br />{" "}
                            <input
                                type="password"
                                name="password1"
                                value={Input.password1}
                                onChange={(e) =>
                                    setInput({
                                        ...Input,
                                        [e.target.name]: e.target.value,
                                    })
                                }
                                id="password1"
                                placeholder="password"
                                defaultValue=""
                                required=""
                            />{" "}
                        </p>
                    </div>
                    <div>
                        <input className='Button' type="submit" defaultValue="submit" />
                    </div>
                    <div className='register'>
                        <button className='Button' onClick={() => navigate2('/RegistrationPage')}>register</button>
                    </div>
                </div>
            </form>
        </div>
    )
}
