import "./MainPage.css";
import RiceOwl from './RiceOwl.png';

import { useEffect, useState } from 'react';

import Topbar from "../components/topbar";
import Post from "../components/Post"
import NewPostSection from "../components/NewPostSection"
import Status from '../components/Status';

import { Posts, PostsLeft } from "../Data"

export default function MainPage() {

    const postarray = Posts
    const [PostArray, SetPostPrray] = useState(PostsLeft)

    const [users, setUsers] = useState();
    const [posts, setPosts] = useState();

    const getUsersData = async () => {
        const response = await fetch(
            "https://jsonplaceholder.typicode.com/users"
        ).then((response) => response.json());
        setUsers(response);
    };

    useEffect(() => {
        getUsersData();
    }, []);
    console.log(users)

    const getPostsData = async () => {
        const response = await fetch(
            "https://jsonplaceholder.typicode.com/posts"
        ).then((response) => response.json());
        setPosts(response);
    };

    useEffect(() => {
        getPostsData();
    }, []);
    console.log(posts)

    const username = localStorage.getItem("username");
    const newuser = localStorage.getItem("newuser");
    console.log(username)
    console.log(newuser)


    return (

        <>
            <header>

                <Topbar />
                <div className="container">
                    <NewPostSection username={localStorage.getItem("username")} postleft={PostArray} setprops={SetPostPrray} />
                    <Status />
                </div>
            </header>
            <div className="pageformat">
                <div className="gridformat">
                    <div className="gridpost" tabIndex={0}>
                        {PostArray.map((p) => (
                            <Post key={p.index} post={p} />
                        ))}
                    </div>
                </div>
                <div className="gridformat">
                    <div className="gridpost" tabIndex={0}>
                        {postarray.map((p) => (
                            <Post key={p.index} post={p} jsonusers={users} />
                        ))}
                    </div>
                </div>

            </div>

        </>

    )
}
