import React, {useState} from 'react'
// import {BrowserRouter as Router, Link} from 'react-router-dom';

import "./LoginPage.css";
import RiceOwl from './RiceOwl.png';
// import background from  './background.webp';


import { Form, Button } from "react-bootstrap"
import RegistrationPage from './RegistrationPage';


export default function LoginPage() {
  const [details, setDetails] = useState({ email: "", password: "" });

  const submitHandler = e => {
    e.preventDefault();
    LoginPage(details)
  }

  return (
    <form onSubmit={submitHandler}>
      <div className='background' style={{ backgroundImage: "url(./background.webp')" }}>
        <div className="login">
            <div className="loginWrapper">
              <div className="loginLeft">
                <img src={RiceOwl} className="logo" alt="Logo" />
                {/* <h3 className="loginLogo">Ricetagram</h3> */}
                <span className="loginDesc">
                  Log In 
                </span>

                <div className="loginBox">
                  <input type="email" name="email" id="email" placeholder="Email" className="loginInput" onChange={e=> setDetails({...details, email: e.target.value})} value={details.email}/>
                </div>

                <div className="loginBox">
                  <input placeholder="Password" type="password" id="password" onChange={e=> setDetails({...details, password: e.target.value})} value={details.password} />
                </div>

                <div className="loginBox">
                  <button className="loginButton">Log In</button>
                </div>
                <div className="loginBox">
                  <input type="submit" value="LOGIN" />
                  {/* <Link to="./RegistrationPage"> */}
                    <button className="loginRegisterButton" component={<RegistrationPage />} to="/RegisrationPage" >
                      Create a New Account
                    </button>

                </div>
              </div>
            </div>
          </div>
      </div>
    </form>
  )
}
